import React, { useEffect, useContext } from "react";
import ProductContext from "../context/ProductContext";
import './css/individual.css';

const Individual = ({ match }) => {
  const { getSingleHero, product } = useContext(ProductContext);
  const { id, name, appearance, className, biography, connections, image, powerstats} = product;

  useEffect(() => {
    getSingleHero(match.params.id);
  }, []);

  console.log(product);

  return (
    <div className="single-hero-container">
      <section className="hero-img">
        <img src={image.url} alt="hero snap" />
        <h1>{name}</h1>
      </section>
      <section className="hero-details">
        <h1>Biography</h1>
        <h4>FullName: {biography.full-name}</h4>
        <h4>Publisher: {biography.publisher}</h4>
        <h4>Aliases: [{biography.aliases.map((name)=>{
          return name &nbsp
        }).join(",")}]</h4>
      </section>
    </div>
  )
};

export default Individual;
