import React, { useEffect, useContext } from "react";
import ProductContext from "../context/ProductContext";
import './css/individual.css';

const Individual = ({ match }) => {
  const { getSingleHero, product } = useContext(ProductContext);

  useEffect(() => {
    getSingleHero(match.params.id);
  }, []);

  console.log(product);

  return (
    <div className="single-hero-container">
      <section className="hero-img">
        <img src="{product.image.url}" alt="hero snap"></img>
      </section>
      <section className="hero-details"></section>
    </div>
  )
};

export default Individual;
