import React, { useEffect, useContext, useState } from "react";
import ProductContext from "../context/ProductContext";
import "./css/individual.css";
import { useParams, useRouteMatch } from "react-router-dom";

const TOKEN = 4231845973529584;
const GET_SINGLE_ITEM_URL = `https://www.superheroapi.com/api.php/${TOKEN}/`;

const Individual = () => {
  let { targetId } = useParams();
  const [single, setSingle] = useState([]);
  const { setLoading } = useContext(ProductContext);

  const getSingleHero = async (id) => {
    try {
      setLoading();
      const resp = await fetch(`${GET_SINGLE_ITEM_URL}${id}`);
      const filterRes = await resp.json();
      setSingle(filterRes);
    } catch (err) {
      console.log(err);
    }
  };

  useEffect(() => {
    getSingleHero(targetId);
  }, [targetId]);

  // const { getSingleHero, product, products } = useContext(ProductContext);
  // const {
  //   id,
  //   name,
  //   appearance,
  //   className,
  //   biography,
  //   connections,
  //   image,
  //   powerstats,
  //   work,
  // } = product;

  return (
    <div className="single-hero-container">
      <section className="hero-img">
        <img src={single.image.url} alt="hero snap" />
      </section>
      <section className="hero-details">
        <h1 style={{ textAlign: "center", fontWeight: "bold" }}>{name}</h1>


      </section>
    </div>
  );
};

export default Individual;
