import React, { useReducer, useEffect } from "react";
import ProductContext from "./ProductContext";
import ProductReducer from "./ProductReducer";

// fetching all cocktails.
const GET_ITEMS_URL = "www.thecocktaildb.com/api/json/v1/1/random.php";

const ProductState = (props) => {
  const initalState = {
    products: [
      {
        id: 1,
        img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTNkNjlT71MsPNM60uTBHt-eTF1SjJN6Nh7CphZy79livNPxbJN9Jj3XjxrdsBLwX5fPb0&usqp=CAU",
        title: "A1",
        detail: "glass shot",
        category: "Alcoholic",
      },
      {
        id: 2,
        img: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTNkNjlT71MsPNM60uTBHt-eTF1SjJN6Nh7CphZy79livNPxbJN9Jj3XjxrdsBLwX5fPb0&usqp=CAU",
        title: "A2",
        detail: "glass shot five",
        category: "Alcoholic",
      },
    ],
    loading: false,
  };

  // initializing reducer.
  const [state, dispatch] = useReducer(ProductReducer, initalState);

  // get items.
  const getItems = async () => {
    const resp = await fetch(GET_ITEMS_URL);
    const filterRes = await resp.json();
    console.log(filterRes);
  };

  // useEffect hooks.
  useEffect(() => {
    getItems();
  }, []);

  return (
    <ProductContext.Provider
      value={{
        products: state.products,
        loading: state.loading,
        getItems,
      }}
    >
      {props.children}
    </ProductContext.Provider>
  );
};

export default ProductState;
